import React, { Component } from "react";
import { Form , Input ,Button} from "semantic-ui-react";

class contributeForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            isloading: false
        }
        this.submitHandler = this.submitHandler.bind(this);
    }
  render() {
    return (
      <Form>
        <Form.Field onSubmit = {this.submitHandler}>
          <label>Amount to contribute</label>
          <Input type="decimal" label="ether" size="massive" labelPosition="right" />
          <Button color="olive" size="large">Contribute</Button>
        </Form.Field>
      </Form>
    );
  }
}

export default contributeForm;
