        return {campaigns};
    }

    renderCampaigns(){
        const items = this.props.campaigns.map(address => {
            return{
                header: address,
                description: <a>View Campaign</a>,
                fluid: true
            }
        });
    }

    render(){
        return(
        <h1>{this.props.campaigns[0]}</h1>
        );
    }
}

