import React, { Component } from 'react';
import {Table, Label , Button} from 'semantic-ui-react';
import web3 from '../ethereum/web3';
import Campaign from '../ethereum/campaign';
class Requestrow extends Component{
    constructor(props){
        super(props);
    }
    render(){
        return(
            <Table.Body>
                <Table.Row>
                <Table.Cell>
                 <Label>{this.props.id}</Label>
                </Table.Cell>
                <Table.Cell>
                 <Label>{this.props.request.description}</Label>
                </Table.Cell>
                <Table.Cell>
                 <Label>{this.props.request.value}</Label>
                </Table.Cell>
                <Table.Cell>
                 <Label>{this.props.request.recipient}</Label>
                </Table.Cell>
                <Table.Cell>
        <Label>{this.props.request.approvalCount}/{this.props.approversCount}</Label>
                </Table.Cell>
                <Table.Cell>
                 <Label><Button size = 'small' color = 'teal'>Yes</Button></Label>
                </Table.Cell>
                <Table.Cell>
                 <Label><Button size = 'small' color = 'green'>Yes</Button></Label>
                </Table.Cell>
            </Table.Row>
            </Table.Body>   
        );
    }
}
export default Requestrow;
