import React, { Component } from "react";
import Layout from "../../components/Layout";
import { Form, Button, Input ,Message} from "semantic-ui-react";
import factory from '../../ethereum/factory';
import web3 from "../../ethereum/web3";
class Campaignnew extends Component {
  constructor(props) {
    super(props);
    this.state = {
      minContribution: "",
      errorMessage:'',
    };
    this.onSubmit = this.onSubmit.bind(this);
  }
  async onSubmit(evt){
    evt.preventDefault();
    try {
      const accounts = await web3.eth.getAccounts();
      await factory.methods.createCampaign(this.state.minContribution).send({
      from : accounts[0]
    });
  console.log('accounts'+ accounts[0]);
  }
    catch(err){
      this.setState({
        errorMessage: err.message,
      });
    }

  }
  render() {
    return (
      <Layout>
        <Form className="text-center" onSubmit = {this.onSubmit}>
          <Form.Field>
            <div className="flex">
              <div className="w-4/5">
                <label className="font-extrabold">Minimum contribution</label>
                <Input className="rounded"
                type='number'
                required
                  value={this.state.minContribution}
                  onChange={(evt) => {
                    this.setState({ minContribution: evt.target.value });
                  }}
                  label="wei"
                  labelPosition="right"
                  size="huge"
                />
              </div>
              <div className="w-1/5 py-6">
                <Button size="huge" color="teal">
                  Create!
                </Button>
              </div>
            </div>
            <h1 className = "text-center font-bold text-red-500">{this.state.errorMessage}</h1>
          </Form.Field>
        </Form>
      </Layout>
    );
  }
}
export default Campaignnew;
