import React, { Component } from 'react';
import {Table, Label , Button} from 'semantic-ui-react';
import web3 from '../ethereum/web3';
import Campaign from '../ethereum/campaign';
class Requestrow extends Component{
    constructor(props){
        super(props);
        this.state = {
            message : '',
            isApprovalLoading : false
        }
        this.approveHandler = this.approveHandler.bind(this);
    }
    async approveHandler(){
        this.setState({
            isApprovalLoading : true
        });
        const campaign = Campaign(this.props.address);
        const accounts = await web3.eth.getAccounts();
        try {
            await campaign.methods.approveRequest(this.props.id - 1).send({
                from: accounts[0]
            });
        } catch (error) {
            this.setState({
                message : error.message,
                isApprovalLoading: false,
            });
        }   
    }

    render(){
        return(
            <Table.Body>
                <Table.Row>
                <Table.Cell>
                 <Label>{this.props.id}</Label>
                </Table.Cell>
                <Table.Cell>
                 <Label>{this.props.request.description}</Label>
                </Table.Cell>
                <Table.Cell>
                 <Label>{this.props.request.value}</Label>
                </Table.Cell>
                <Table.Cell>
                 <Label>{this.props.request.recipient}</Label>
                </Table.Cell>
                <Table.Cell>
        <Label>{this.props.request.approvalCount}/{this.props.approversCount}</Label>
                </Table.Cell>
                <Table.Cell>
                 <Label><Button size = 'small' color = 'teal' onClick = {this.approveHandler} loading = {this.state.isApprovalLoading}>Yes</Button></Label>
                </Table.Cell>
                <Table.Cell>
                 <Label><Button size = 'small' color = 'green' onClick = {this.finalizeHandler} loading = {this.state.isFinalizeLoading}>Yes</Button></Label>
                </Table.Cell>
            </Table.Row>
            </Table.Body>   
        );
    }
}
export default Requestrow;
