import React, { Component } from "react";
import Campaign from "../../ethereum/campaign";
import { Link } from "../../routes";
import { Button ,Table} from "semantic-ui-react";
import Layout from "../../components/Layout";
class RequestIndex extends Component {
  static async getInitialProps(props) {
    console.log("this is " + props.query.address);
    const campaign = Campaign(props.query.address);
    const requestcount = await campaign.methods.gatRequestsCount().call();
    const requests = await Promise.all(
      Array(parseInt(requestcount))
        .fill()
        .map((element, index) => {
          return campaign.methods.requests(index).call();
        })
    );
    return { address: props.query.address, requests, requestcount };
  }
  render() {
    const {Header, Row , Headercell , Body} = Table;
    return (
      <Layout>
        <div className="text-center">
          <Link route={`/campaigns/show/${this.props.address}/request/new`}>
            <Button size="mini" color="facebook">
              New request +
            </Button>
          </Link>

          <Table></Table>
        </div>
      </Layout>
    );
  }
}
export default RequestIndex;
