import React, { Component } from "react";
import {card} from 'semantic-ui-react';
import Layout from "../../components/Layout";
import Campaign from "../../ethereum/Campaign";

class show extends Component {
  static async getInitialProps(props) {
    const campaign = await Campaign(props.query.address);

    const summary = await campaign.methods.getSummary().call();

    console.log(summary);

    return {
      minimumContribution: summary[0],
      balance: summary[1],
      requestsCount: summary[2],
      approversCount: summary[3],
      manager: summary[4],
    };
  }

  render() {

    return (
      <Layout>
        <div className=" text-center rounded overflow-hidden shadow-2xl">
          <div className="p-10 m-20">

          <div className="w-1/2 text-center rounded overflow-hidden shadow-2xl">
          <div className="p-2 m-2">
            <div className="font-bold text-xs mb-2">Manager : {this.props.manager}</div>
            </div>
            </div>

            <div className="w-1/2 text-center rounded overflow-hidden shadow-2xl">
          <div className="p-2 m-2">
          <p className="text-green-700 text-base">
              Contract Balance : {this.props.balance}
            </p>            </div>
            </div>

            <div className="w-1/2 text-center rounded overflow-hidden shadow-2xl">
          <div className="p-2 m-2">
          <p className="text-red-700 text-base">
              Number of requests : {this.props.requestsCount}
            </p>            </div>
            </div>

            <div className="w-1/2 text-center rounded overflow-hidden shadow-2xl">
          <div className="p-2 m-2">
          <p className="text-green-700 text-base">
              Approvers : {this.props.approversCount}
            </p>            </div>
            </div>

            <div className="w-1/2 text-center rounded overflow-hidden shadow-2xl">
          <div className="p-2 m-2">
            <div className="font-bold text-xs mb-2">Manager : {this.props.manager}</div>
            </div>
            </div>





            <p className="text-green-700 text-xl font-bold text-base">
              Minimum Contribution: {this.props.minimumContribution}
            </p>
          </div>
        </div>
      </Layout>
    );
  }
}

export default show;
