import React, { Component } from "react";
import Campaign from "../ethereum/campaign";
import {Link} from 'semantic-ui-react';


class Projectdetails extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  async componentDidMount(props) {
    console.log("from here" + props);

    const campaign = await Campaign(this.props.address);
    const summary = await campaign.methods.getSummary().call();

    console.log("ingirundhu", summary);
    this.setState({
      projectTitle: summary[5],
      projectDescription: summary[6],
      projectPromise: summary[7],
    });
  }

  render() {
    return (
      <div>
          <link
          rel="stylesheet"
          href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css"
        />
        <div className="max-w-sm rounded overflow-hidden shadow-lg">
          <div className="px-6 py-4">
            <div className="font-bold text-xl mb-2">{this.state.projectTitle}</div>
            <p className="text-gray-700 text-base">
              {this.state.projectDescription}
            </p>
          </div>
          <div className="px-6 py-4">
            <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2">
              {this.state.projectPromise}
            </span>

            <Link route="/Campaigns/new">
            <Button
              content="Create Campaign"
              icon="add circle"
              floated="right"
              color="olive"
            />
          </Link>
          </div>
        </div>
      </div>
    );
  }
}
export default Projectdetails;
